/* Hardware Info
 * // ******* Required Hardware I/O connections*******************
 * // Slide pot pin 1 connected to ground
 * // Slide pot pin 2 connected to PE2/AIN1
 * // Slide pot pin 3 connected to +3.3V
 * // fire button connected to PE0
 * // special weapon fire button connected to PE1
 * // 8*R resistor DAC bit 0 on PB0 (least significant bit)
 * // 4*R resistor DAC bit 1 on PB1
 * // 2*R resistor DAC bit 2 on PB2
 * // 1*R resistor DAC bit 3 on PB3 (most significant bit)
 * // LED on PB4
 * // LED on PB5
 *
 * // Red SparkFun Nokia 5110 (LCD-10168)
 * // -----------------------------------
 * // Signal        (Nokia 5110) LaunchPad pin
 * // 3.3V          (VCC, pin 1) power
 * // Ground        (GND, pin 2) ground
 * // SSI0Fss       (SCE, pin 3) connected to PA3
 * // Reset         (RST, pin 4) connected to PA7
 * // Data/Command  (D/C, pin 5) connected to PA6
 * // SSI0Tx        (DN,  pin 6) connected to PA5
 * // SSI0Clk       (SCLK, pin 7) connected to PA2
 * // back light    (LED, pin 8) not connected, consists of 4 white LEDs which draw ~80mA total
 */

/*************** GENERAL PURPOSE DEFINES ************/
#define CLOCK_SPEED    80000000L
#define TRUE           (1)
#define FALSE          (0)
#define ON             (1)
#define OFF            (0)
#define LEFT           (-1)           // x-pos offset for left movement
#define RIGHT          (1)            // x-pos offset for right movement
#define UP             (-1)           // y-pos offset for up movement
#define DOWN           (1)            // y-pos offset for down movement
#define BIT(X)     (1L << (X))        // single bit set
#define PCTL(X)    (7L << (X * 3))    // three bits set

// General and intialisation functions
void DisableInterrupts(void);         // Disable interrupts
void EnableInterrupts(void);          // Enable interrupts
void WaitForInterrupt(void);          // Wait for interrupts
void Timer2_Init(unsigned long period);
void Delay100ms(unsigned long count); // time delay in 0.1 seconds
void init(void);

/************************************ GAME DATA *******************************************/

#define ALIEN_COLS             5  // number of aliens in each row
#define ALIEN_ROWS             3  // number of aliens in each column
#define BUNKERS                2  // number of bunkers
#define PMISSILES              10 // number of concurrent missiles a player can fire
#define AMISSILES              4  // number of concurrent missiles aliens can fire
#define MAX_SCORE_DIGITS       6

// Use these parameters to adjust the chance of firing a missile
#define RAND_MAX               255 // Maximum value returned by rand function
#define RAND_BASE_REDUCTION    220 // MAX - REDUCTION = base chance for alien to fire a missile (255 - 220 = 35 = ~14%)
#define RAND_MISSILE_WEIGHT    5   // reduced further by missiles already fired (5 * 3 = 15 = ~6%)
#define RAND_ALIEN_WEIGHT      2   // reduced further by existing aliens as more can randomly fire (15 * 2 = 30 = ~12%)

#define MAX_POWER              4

// Sprites
typedef struct Sprite {
    int                  x_pos;
    int                  y_pos;
    int                  width;
    int                  height;
    int                  exists;
    int                  bitmapn;
    int                  exploding_at;
    int                  score;
    const unsigned char *bitmap[4];
} Sprite;

Sprite  score_digits[MAX_SCORE_DIGITS];
Sprite  player;
Sprite  powerbar;
Sprite  hearts;
Sprite  aliens[ALIEN_COLS][ALIEN_ROWS];
Sprite  mothership;
Sprite  bunkers[BUNKERS];
Sprite  player_missiles[PMISSILES];
Sprite  alien_missiles[AMISSILES];
Sprite *sprite_list[MAX_SCORE_DIGITS + 1 + 1 + 1
                    + (ALIEN_COLS * ALIEN_ROWS) + 1 + BUNKERS + PMISSILES
                    + AMISSILES];
const int nsprites = (sizeof(sprite_list) / sizeof(sprite_list[0]));

// Global game parameters
int AlienSpeed;
int AlienInitialSpeed = 16;
int alien_direction;
int score;
int level = 1;
int lives;
int power_up    = 0;
int power_level = MAX_POWER;

// Game functions
void init_sprites(void);
void draw_board(void);
void reset_board(void);
void move_alien_missiles(void);
void move_player_missiles(void);
void move_player(void);
void move_mothership(void);
void move_sprites(void);
void explode_alien(Sprite *alien);
void explode_player(void);
void update_score(int value);
void damage_bunker(Sprite *bunker);
void destroy(Sprite *s);
void show_level(void);
void game_over(void);
int  sprite_collision(Sprite *s1, Sprite *s2);

/*********************************** GRAPHICS ******************************************/

