/* display the level at start of level */
void show_level(void){
	Nokia5110_ClearBuffer();
	Nokia5110_DisplayBuffer();
	Nokia5110_SetCursor(5,2);
	Nokia5110_OutUDec(level);
	Nokia5110_SetCursor(2,2);
	Nokia5110_OutString("Level");
	Delay100ms(25); // delay 2.5 seconds
}
/* return number of active aliens */
int count_aliens(void){
	int i,j;
	int count = mothership.exists;
	for (i=0; i<ALIEN_COLS; i++)
		for (j=0; j<ALIEN_ROWS; j++)
			if (aliens[i][j].exists && aliens[i][j].bitmapn<aliens[i][j].exploding_at)
				count ++;
	return count;
}

/* return number of active alien missiles */
int count_alien_missiles(void){
	int i;
	int count = 0;
	for (i=0; i<AMISSILES; i++)
		count += alien_missiles[i].exists;
	return count;
}

/* return number of active player missiles */
int count_player_missiles(void){
	int i;
	int count = 0;
	for (i=0; i<PMISSILES; i++)
		count += player_missiles[i].exists;
	return count;
}

/* start a player explosion sequence */
void explode_player(void){
	player.bitmapn=player.exploding_at;
	hearts.bitmapn = 4-lives;
	Sound_Tone(EXPLOSION_SOUND);
}

/* update the on-screen score digit sprites */
void update_score(int value){
	int i=0;
	int number;
	score += value;
	number = score;
	while (number > 0 && i < MAX_SCORE_DIGITS)
	{
		score_digits[i].exists = TRUE;
		score_digits[i++].bitmap[0] = digitbmp[number%10];
		number /= 10;
	}
}
/* start the destruction of an alien ship */
void explode_alien(Sprite* alien){
	int alien_count = count_aliens() - mothership.exists;
	alien->bitmapn=alien->exploding_at;
	update_score(alien->score);
	Sound_Tone(INVADER_KILLED_SOUND);
	if (alien != &mothership)
	{
		switch (alien_count - 1)
		{
			case 1:
			case 2:
				AlienSpeed = AlienSpeed/2+1;
				break;
			case ALIEN_COLS*ALIEN_ROWS*1/3:
			case ALIEN_COLS*ALIEN_ROWS*1/2:
			case ALIEN_COLS*ALIEN_ROWS*3/4:
				if (AlienSpeed>2) AlienSpeed--;
				break;
			default:
				break;
		}
	}
}

/* reset board to start of level state */
void reset_board(void){
	int i,j;
  // Reset player
	for (i=0; i<BUNKERS; i++)
	{
		bunkers[i].exists = TRUE;
	}
	// Reset alien positions
	destroy(&mothership);
	alien_direction = LEFT;
	AlienSpeed = AlienInitialSpeed;
	for (i=0; i<ALIEN_COLS; i++)
	{
		int y_pos = 0;
		for (j=0; j<ALIEN_ROWS; j++)
		{
			y_pos += aliens[i][j].height;
			aliens[i][j].exists = TRUE;
			aliens[i][j].x_pos = i*(aliens[i][j].width+1)+SCREENW-(ALIEN_COLS*(aliens[i][j].width+1))-1;
			aliens[i][j].y_pos = y_pos++;
		}
	}
	// clear any missiles
	for (i=0; i<PMISSILES; i++)
		destroy(&player_missiles[i]);
	for (i=0; i<AMISSILES; i++)
		destroy(&alien_missiles[i]);
}

/* check if any aliens are present on screen */
int aliens_exist (void){
	int i,j;
	if (mothership.exists) return TRUE;
	for (i=0; i<ALIEN_COLS; i++)
		for (j=0; j<ALIEN_ROWS; j++)
			if (aliens[i][j].exists) return TRUE;
	return FALSE;
}

/* update bunker damaged state */
void damage_bunker (Sprite *bunker){
	if (bunker->exists)
	{
		if (bunker->bitmapn < 2)
			bunker->bitmapn++;
		else
			destroy(bunker);
	}
}

/* change alien direction at end of a row */
void change_alien_direction(int direction){
	int i,j;
	int max_y=0;
	alien_direction = direction;
	for (i=0; i<ALIEN_COLS;i++)
		for (j=0; j<ALIEN_ROWS; j++)
		{
			if (aliens[i][j].exists && aliens[i][j].bitmapn<aliens[i][j].exploding_at)
			{
				aliens[i][j].y_pos += DOWN;
				if (aliens[i][j].y_pos > max_y)
					max_y = aliens[i][j].y_pos;
			}
		}
	if (max_y >= bunkers[0].y_pos-bunkers[0].height)
	{
		for (i=0; i< BUNKERS; i++)
			damage_bunker(&bunkers[i]);
	}
	if (max_y > player.y_pos-player.height)
	{
		explode_player();
		// move aliens to top row again
		destroy(&mothership);
		for (i=0; i<ALIEN_COLS; i++)
		{
			int y_pos = 0;
			for (j=0; j<ALIEN_ROWS; j++)
			{
				y_pos += aliens[i][j].height;
				aliens[i][j].x_pos = i*(aliens[i][j].width+1)+SCREENW-(ALIEN_COLS*(aliens[i][j].width+1))-1;
				aliens[i][j].y_pos = y_pos++;
			}
		}
	}
}

/* create any alien missiles randomly */
void create_alien_missiles(void){
	int i,j;
	// random value, reduced by number of aliens present, reduced by number of alien missiles already spawned
	int threshold = RAND_MAX-RAND_BASE_REDUCTION-count_aliens()*RAND_ALIEN_WEIGHT-count_alien_missiles()*RAND_MISSILE_WEIGHT;
	for (i=0; i<ALIEN_COLS; i++)
	{
		for (j=ALIEN_ROWS-1; j>=0; j--)
		{
			if (aliens[i][j].exists)
			{
				if (aliens[i][j].bitmapn < 2)
				{
					if ((int)Random() <= threshold)
					{
						int k=0;
						while (alien_missiles[k].exists) k++;
						alien_missiles[k].x_pos = aliens[i][j].x_pos+(aliens[i][j].width-alien_missiles[k].width)/2;
						alien_missiles[k].y_pos = aliens[i][j].y_pos;
						alien_missiles[k].exists = TRUE;
					}
				}
				break;
			}
		}
		if (count_alien_missiles() == AMISSILES) break;
	}
}

/* update mothership position */
void move_mothership(void){
	if (mothership.exists)
	{
		if (mothership.x_pos == SCREENW-mothership.width-1)
			destroy(&mothership);
		else if (mothership.bitmapn == 1)
			mothership.bitmapn = 2;
		else if (mothership.bitmapn == 2)
			mothership.bitmapn = 3;
		else if (mothership.bitmapn == 3)
			destroy(&mothership);
		else
			mothership.x_pos += RIGHT;
	// if no other noise is playing use the mothership sound
		Sound_Tone(MOTHERSHIP_SOUND);
	}
	else
	{
		int i,j;
		int min_y=47;
		// find topmost alien
		for (i=0; i<ALIEN_COLS; i++)
			for (j=0; j<ALIEN_ROWS; j++)
				if (aliens[i][j].exists)
					if (aliens[i][j].y_pos < min_y)
						min_y = aliens[i][j].y_pos - aliens[i][j].height;
		// If there is room for a mothership above the topmost alien
		if (min_y >= mothership.height)
		{ // Then there is a 3% chance each tick
			if (Random()<(RAND_MAX/32))
			{ // to spawn a mothership
				mothership.exists = TRUE;
				mothership.x_pos = 0;
				mothership.y_pos = mothership.height;
			}
		}
	}
}

/* update alien positions */
void move_aliens(void){
	/*
   For each living alien,
		1. move it to it's new position
				- detect end of row, reverse direction and drop down
		2. change the animation, progress alien explosions and delete exploded aliens
		3. possibly fire an alien missile
  */
	int i,j,start,end,step;
	if (alien_direction == LEFT)
	{
		start = 0;
		end = ALIEN_COLS;
		step = 1;
	}
	else
	{
		start = ALIEN_COLS-1;
		end = -1;
		step = -1;
	}
	for (i=start; i!=end; i+=step)
	{
		for (j=0; j<ALIEN_ROWS; j++)
		{
			if (aliens[i][j].exists)
			{
				if (alien_direction == LEFT && aliens[i][j].x_pos == 0)
					change_alien_direction(RIGHT);
				if (alien_direction == RIGHT && aliens[i][j].x_pos == SCREENW-aliens[i][j].width-1)
					change_alien_direction(LEFT);
				switch (aliens[i][j].bitmapn)
				{
					case 0: aliens[i][j].bitmapn=1; break;
					case 1: aliens[i][j].bitmapn=0; break;
					case 2: aliens[i][j].bitmapn=3; break;
					case 3: destroy(&aliens[i][j]); break;
					default: break; // Should never get here!!
				}
				aliens[i][j].x_pos += alien_direction;
			}
		}
	}
	create_alien_missiles();
	// Play the default invader noise
	Sound_Tone(INVADER_SOUND);
}

/* Create a player-initiated laser missile */
void create_player_missile(void){
		int missiles = count_player_missiles();
		if (missiles < PMISSILES)
		{
			int k,free;
			int max_y = 0;
			for (k=0; k<PMISSILES; k++)
			{
				if (player_missiles[k].exists)
				{
					if (player_missiles[k].y_pos > max_y)
						max_y = player_missiles[k].y_pos;
				}
				else
					free = k;
			}
			if ((max_y < SCREENH-player.height-player_missiles[free].height-1) ||
					(power_up && max_y < SCREENH-player.height-player_missiles[free].height+1))
			{
				player_missiles[free].x_pos = player.x_pos+(player.width-player_missiles[free].width)/2+1;
				player_missiles[free].y_pos = player.y_pos-player.height+player_missiles[free].height-1;
				player_missiles[free].exists = TRUE;
				Sound_Tone(SHOOT_SOUND);
				LED2(TRUE);
			}
		}
}
/* handle player and embedded powerbar sprite update and laser and auto-fire */
void move_player(void){
	// Read ADC value
	static int last_fire_button=0;
	int x_pos=ADCdata-(player.width>>1);
	if (player.bitmapn==player.exploding_at)
		player.bitmapn=2;
	if (player.bitmapn==2 && !(TimerCount2A%32))
		player.bitmapn=3;
	if (player.bitmapn==3 && !(TimerCount2A%32))
	{
		destroy(&player);
		destroy(&powerbar);
	}
	else
	{
	// Convert to screen position
		if (x_pos < 0)
			x_pos = 0;
		else if (x_pos > (SCREENW-player.width))
			x_pos = (SCREENW-player.width);
		player.x_pos = x_pos;
		if ((GPIO_PORTE_DATA_R & BIT(1)) || power_up )
		{
			if (power_level==MAX_POWER)
				power_up = TRUE;
			if (power_up)
			create_player_missile();
		}
		// Read fire button and compare with last time
		else if (GPIO_PORTE_DATA_R & BIT(0))
		{
			if (!last_fire_button)
			{
				// create missile
				create_player_missile();
				last_fire_button = TRUE;
			}
		}
		else
			last_fire_button = FALSE;
	}
	// update embedded power bar
	powerbar.x_pos = player.x_pos;
	// power bar is incremented every 2048th tick while power up is charging ~40 seconds to charge
	if (!power_up && !(TimerCount2A & 0x3FF))
	{
		if (power_level<MAX_POWER) power_level++;
		powerbar.bitmapn = power_level-1;
		powerbar.exists = TRUE;
	}
	// power bar is decremented every 256th tick while power up is active ~4 seconds active
	if (power_up && !(TimerCount2A & 0x3F))
	{
		if (power_level>0)
			power_level--;
		else
			power_up = FALSE;
		powerbar.bitmapn = power_level-1;
		powerbar.exists = (power_level>0);
	}
}
/* Detect whether two sprites overlap */
int sprite_collision(Sprite *s1, Sprite *s2){
	if ((s1->x_pos < s2->x_pos + s2->width) &&
			(s1->x_pos + s1->width > s2->x_pos) &&
			(s1->y_pos > s2->y_pos - s2->height) &&
			(s1->y_pos - s1->height < s2->y_pos))
		return TRUE;
	return FALSE;
}
/* Turn off a sprite */
void destroy(Sprite *s){
	s->exists=0;
	s->bitmapn=0;
}
/* Test whether the player has been hit by an alien missile */
void check_player_hit(Sprite *missile){
	if (player.bitmapn < player.exploding_at)
		if (sprite_collision(missile,&player))
		{
			explode_player();
			destroy(missile);
		}
}
/* Test whether an alien has been hit by a player missile */
void check_alien_hit(Sprite *missile){
	int i,j;
	for (i=0; i<ALIEN_COLS; i++)
		for (j=0; j<ALIEN_ROWS; j++)
			if (aliens[i][j].exists && (aliens[i][j].bitmapn < aliens[i][j].exploding_at))
				if (sprite_collision(missile,&aliens[i][j]))
				{
					explode_alien(&aliens[i][j]);
					destroy(missile);
					return;
				}
	if(mothership.exists && (mothership.bitmapn < mothership.exploding_at))
		if (sprite_collision(missile,&mothership))
		{
			explode_alien(&mothership);
			destroy(missile);
		}
}
/* Test whether a bunker has been hit by an alien missile */
void check_bunker_hit(Sprite *missile){
	int i;
	for (i=0; i<BUNKERS; i++)
		if (bunkers[i].exists)
			if (sprite_collision(missile,&bunkers[i]))
			{
				damage_bunker(&bunkers[i]);
				destroy(missile);
				return;
			}
}
/* Test whether a player missile has been hit by an alien missile */
void check_missile_collision(Sprite *missile){
	int i;
	for (i=0; i<AMISSILES; i++)
		if (alien_missiles[i].exists)
			if (sprite_collision(missile,&alien_missiles[i]))
			{
				destroy(missile);
				destroy(&alien_missiles[i]);
				return;
			}
}
/* update active player missiles */
void move_player_missiles(void){
	int i;
	for (i=0; i<PMISSILES; i++)
	{
		// move player missile
		if (player_missiles[i].exists)
		{
			// detect player missile collision with aliens/bunkers
			check_bunker_hit(&player_missiles[i]);
			check_missile_collision(&player_missiles[i]);
			check_alien_hit(&player_missiles[i]);
			if (player_missiles[i].y_pos == player_missiles[i].height)
				destroy(&player_missiles[i]);
			else
			{
				player_missiles[i].y_pos += UP;
				player_missiles[i].bitmapn = 1-player_missiles[i].bitmapn;
			}
		}
	}
}
/* update active alien missiles */
void move_alien_missiles(void){
	int i;
	// move alien missiles
	for (i=0; i<AMISSILES; i++)
	{
		if (alien_missiles[i].exists)
		{
			if (alien_missiles[i].y_pos == SCREENH-2)
				destroy(&alien_missiles[i]);
			else
			{
				alien_missiles[i].y_pos += DOWN;
				alien_missiles[i].bitmapn = 1-alien_missiles[i].bitmapn;
			}
		}
	// detect alien missile collision with player/bunkers
		if (alien_missiles[i].exists)
		{
			check_bunker_hit(&alien_missiles[i]);
			check_player_hit(&alien_missiles[i]);
			// no need to check missile collisions as player missile will check this and is updated faster
		}
	}
}
/* Update sprites at appropriate intervals */
void move_sprites(void){
	// player position is updated every tick
	move_player();
	// player missiles are updated every 3rd tick
	if (!(TimerCount2A % 2))
		move_player_missiles();
	// alien positions are updated every ? tick
	if (!(TimerCount2A % AlienSpeed))
		move_aliens();
	// mothership is updated every 8th tick
	if (!(TimerCount2A % 4))
		move_mothership();
	// enemy missiles are updated every 8th tick
	if (!(TimerCount2A % 6))
		move_alien_missiles();
}
/* Output a bitmap to the screen buffer */
void myNokia5110_PrintBMP(unsigned char xpos, unsigned char ypos, const unsigned char *ptr, unsigned char threshold){
  long width = ptr[BMP_WIDTH], height = ptr[BMP_HEIGHT], i, j;
  unsigned short screenx, screeny;
  unsigned char mask;
  // check for clipping
  if((height <= 0) ||              // bitmap is unexpectedly encoded in top-to-bottom pixel order
     ((width%2) != 0) ||           // must be even number of columns
     ((xpos + width) > SCREENW) || // right side cut off
     (ypos < (height - 1)) ||      // top cut off
     (ypos > SCREENH))           { // bottom cut off
		 return;
  }
  if(threshold > 14){
    threshold = 14;             // only full 'on' turns pixel on
  }
  // bitmaps are encoded backwards, so start at the bottom left corner of the image
  screeny = ypos/8;
  screenx = xpos + SCREENW*screeny;
  mask = ypos%8;                // row 0 to 7
  mask = 0x01<<mask;            // now stores a mask 0x01 to 0x80
  j = ptr[10];                  // byte 10 contains the offset where image data can be found
  for(i=1; i<=(width*height/2); i=i+1){
    // the left pixel is in the upper 4 bits
    if(((ptr[j]>>4)&0xF) > threshold)
      Screen[screenx] |= mask;
    screenx = screenx + 1;
    // the right pixel is in the lower 4 bits
    if((ptr[j]&0xF) > threshold)
      Screen[screenx] |= mask;
    screenx = screenx + 1;
    j = j + 1;
    if((i%(width/2)) == 0){     // at the end of a row
      if(mask > 0x01){
        mask = mask>>1;
      } else{
        mask = 0x80;
        screeny = screeny - 1;
      }
      screenx = xpos + SCREENW*screeny;
      // bitmaps are 32-bit word aligned
      switch((width/2)%4){      // skip any padding
        case 0: j = j + 0; break;
        case 1: j = j + 3; break;
        case 2: j = j + 2; break;
        case 3: j = j + 1; break;
      }
    }
  }
}
/* Draw all active sprites */
void draw_board(void){
	int i;
	Nokia5110_ClearBuffer();
	for (i=0; i<nsprites; i++)
		if (sprite_list[i]->exists)
			myNokia5110_PrintBMP(	sprite_list[i]->x_pos,
													sprite_list[i]->y_pos,
													sprite_list[i]->bitmap[sprite_list[i]->bitmapn],
													0);
  Nokia5110_DisplayBuffer();     // draw buffer
}
/* Hardware, Game and Sprite Initialisation */
void init (void){
  TExaS_Init(SSI0_Real_Nokia5110_Scope);  // set system clock to 80 MHz
  Random_Init(1);
	DAC_Init();
	ADC0_Init();
	Sound_Init();
	Button_Init();
	LED_Init();
  Nokia5110_Init();
	score = 0;
	lives = 3;
	init_sprites();
	Timer2_Init(CLOCK_SPEED/60); // Create 60Hz interrupt
}
/* Display the GAME OVER screen */
void game_over(void){
	Nokia5110_Clear();
  Nokia5110_SetCursor(1, 1);
  Nokia5110_OutString("GAME OVER");
  Nokia5110_SetCursor(1, 2);
  Nokia5110_OutString("Nice try,");
  Nokia5110_SetCursor(1, 3);
  Nokia5110_OutString("Earthling!");
  Nokia5110_SetCursor(2, 4);
  Nokia5110_OutUDec(score);
}

/* Main program */
int main(void){
	int i;
	init();
	// Display Space Invaders title screen
	Nokia5110_PrintBMP(	0,SCREENH-1,Marqueecjl,0);
	Nokia5110_DisplayBuffer();
	Delay100ms(20); // delay 2 seconds
  // Initialise the game board
	reset_board();
	show_level();
	while (lives > 0)
	{
		player.exists = TRUE;
		powerbar.exists = TRUE;
		powerbar.bitmapn = power_level - 1;
		while (player.exists)
		{
			while (aliens_exist() && player.exists)
			{
				while (!Semaphore2A) WaitForInterrupt();
				Semaphore2A = FALSE;
				move_sprites();
				draw_board();
			}
			if (!player.exists)
			{
				Delay100ms(1); // delay 0.1 seconds
				// remove a life and any missiles
				for (i=0; i<AMISSILES; i++)
					destroy(&alien_missiles[i]);
				for (i=0; i<PMISSILES; i++)
					destroy(&player_missiles[i]);
				lives--;
			}
			else
			{
				// Speed up the aliens and re-initialise the game board
				if (--AlienInitialSpeed == 1) AlienInitialSpeed = 2;
				reset_board();
				level +=1;
				show_level();
			}
		}
	}
	Delay100ms(5); // delay 0.5 seconds
	game_over();
	while(1){}
}
/* Initialise Timer 2 used for main control loop*/
void Timer2_Init(unsigned long period){
  unsigned long volatile delay;
  SYSCTL_RCGCTIMER_R |= SYSCTL_RCGCTIMER_R2; // 0a) activate timer2
//  SYSCTL_RCGCTIMER_R |= SYSCTL_RCGCTIMER_R3; // 0b) activate timer3
  delay = SYSCTL_RCGCTIMER_R;
// configure timer 2A
  TimerCount2A = 0;
  Semaphore2A = 0;
  TIMER2_CTL_R = 0x00000000;              // 1a) disable timer2 during setup
  TIMER2_CFG_R = TIMER_CFG_32_BIT_TIMER;  // 2a) configure timer2 for 32-bit mode
	TIMER2_TAMR_R |= TIMER_TAMR_TAMR_PERIOD; // 3a) periodic count down is default but needed by simulator
  TIMER2_TAILR_R = period-1;              // 4a) timer2A reload value
  TIMER2_TAPR_R = 0;                      // 5a) timer2A bus clock resolution
  TIMER2_ICR_R |= TIMER_ICR_TATOCINT;     // 6a) clear timer2A timeout flag
  TIMER2_IMR_R |= TIMER_IMR_TATOIM;       // 7a) arm timer2A timeout interrupt
	TIMER2_CTL_R |= TIMER_CTL_TAEN;         // 10a) enable timer2A
// interrupts are enabled in the main program after all devices initialized
// vector number 39, interrupt number 23
  NVIC_PRI5_R =                           // 8) priority 4
			(NVIC_PRI5_R & (~NVIC_PRI5_INT23_M))
			| (4UL << NVIC_PRI5_INT23_S);
  NVIC_EN0_R = 1<<23;                     // 9) enable IRQ 23 in NVIC
}
/* Update player position ADC data and enable main loop to run */
void Timer2A_Handler(void){
  TIMER2_ICR_R = TIMER_ICR_TATOCINT;   // acknowledge timer2A timeout
  TimerCount2A++;
  Semaphore2A = 1; // trigger
//  ADCdata = (((long)ADC0_In()-515L)*28L)>>10;    // 3 - collect an ADC sample of player x pos
	ADCdata = 66 - ADC0_In() * (SCREENW - BMP_WIDTH) / 4095;
}
/* General purpose 100ms delay */
void Delay100ms(unsigned long count){unsigned long volatile time;
  while(count>0){
    time = 727240;  // 0.1sec at 80 MHz
    while(time){
	  	time--;
    }
    count--;
  }
}
/* Initialise the player position ADC hardware */
void ADC0_Init(void){
// This initialization function sets up the ADC
// Max sample rate: <=125,000 samples/second
// SS3 triggering event: software trigger
// SS3 1st sample source:  channel 1
// SS3 interrupts: enabled but not promoted to controller
	volatile unsigned long delay;
	SYSCTL_RCGC2_R |= BIT(4);         // Enable GPIO Port E clock
	delay = SYSCTL_RCGC2_R;
	GPIO_PORTE_DIR_R &= ~(BIT(2));    // PE2 is input
	GPIO_PORTE_DEN_R &= ~(BIT(2));    // Disable digital input on PE2
	GPIO_PORTE_AFSEL_R |= BIT(2);     // Enable PE2 alternate function
	GPIO_PORTE_AMSEL_R |= BIT(2);     // Enable PE2 analog functions
	GPIO_PORTE_PCTL_R &= ~(PCTL(2));    // Clear PCTL content for PE2
  SYSCTL_RCGC0_R |= BIT(16);        // Enable ADC clock
	delay = SYSCTL_RCGC0_R;
	SYSCTL_RCGC0_R &= ~(SYSCTL_RCGC0_ADC0SPD_M); // Max Sample speed MAXADCSPD = 125 KHz
	SYSCTL_RCGC0_R |= SYSCTL_RCGC0_ADC0SPD_125K;
	ADC0_SSPRI_R = 0x3210L;           // SS3 is lowest priority
	ADC0_EMUX_R &= ~(0xF000L);        // EM3 is software triggered
	ADC0_SSCTL3_R = (BIT(1)|BIT(2));  // Interrupt enabled IE0=1, END0=1, TS0=0, D0=0
	ADC0_PSSI_R &= ~(BIT(3));         // Clear S/W request for SS3 sample
	ADC0_ACTSS_R |= BIT(3);           // Enable sequencer 3
	ADC0_SSMUX3_R = (ADC0_SSMUX3_R & ~(0xF)) | 1L;
	ADC0_ISC_R |= BIT(3);             // Clear SS3 ADC reading ready flag
}
/* Initialise the game buttons */
void Button_Init(void){
	/* Fire button is positive logic switch on PE0 */
	volatile unsigned long delay;
	SYSCTL_RCGC2_R |= SYSCTL_RCGC2_GPIOE; // Enable GPIO Port E clock
	delay = SYSCTL_RCGC2_R;
	// PE0 is fire button
	GPIO_PORTE_DIR_R &= ~(BIT(0));        // PE0 is input
	GPIO_PORTE_DEN_R |= BIT(0);           // Enable digital input on PE0
	GPIO_PORTE_PDR_R |= BIT(0);           // Enable pull-down resistor on PE0
	GPIO_PORTE_AFSEL_R &= BIT(0);         // Disable PE0 alternate function
	GPIO_PORTE_AMSEL_R &= BIT(0);         // Disable PE0 analog functions
	GPIO_PORTE_PCTL_R &= ~(PCTL(0));      // Clear PCTL content for PE0
	// PE1 is special weapon
	GPIO_PORTE_DIR_R &= ~(BIT(1));        // PE1 is input
	GPIO_PORTE_DEN_R |= BIT(1);           // Enable digital input on PE1
	GPIO_PORTE_PDR_R |= BIT(1);           // Enable pull-down resistor on PE1
	GPIO_PORTE_AFSEL_R &= BIT(1);         // Disable PE1 alternate function
	GPIO_PORTE_AMSEL_R &= BIT(1);         // Disable PE1 analog functions
	GPIO_PORTE_PCTL_R &= ~(PCTL(1));      // Clear PCTL content for PE1
}
/* Request and wait for an ADC sample */
unsigned long ADC0_In(void){
//------------ADC0_In------------
// Busy-wait Analog to digital conversion
// Input: none
// Output: 12-bit result of ADC conversion
	unsigned long ADC_value;
	ADC0_PSSI_R |= BIT(3);              // Request SS3 reading
	while (!(ADC0_RIS_R & BIT(3))){};   // wait for ADC sample capture
	ADC_value = ADC0_SSFIFO3_R & 0xFFF; // get sample
	ADC0_ISC_R |= BIT(3);               // Clear SS3 ADC reading ready flag
  return (ADC_value);
}
/* Initialise sound DAC */
void DAC_Init(void){
// **************DAC_Init*********************
// Initialize 4-bit DAC
// Input: none
// Output: none
	volatile unsigned long delay;
	SYSCTL_RCGC2_R |= GPIO_PORTB;
	delay = SYSCTL_RCGC2_R;
	GPIO_PORTB_AMSEL_R &= ~(DAC_PINS);
	GPIO_PORTB_AFSEL_R &= ~(DAC_PINS);
	GPIO_PORTB_DIR_R   |=   DAC_PINS;
	GPIO_PORTB_PCTL_R  &= ~(DAC_PCTL_PINS);
	GPIO_PORTB_DR8R_R  |=   DAC_PINS;
	GPIO_PORTB_DEN_R   |=   DAC_PINS;
}
/* Output a single sound DAC step */
void DAC_Out(unsigned long data){
// **************DAC_Out*********************
// output to DAC
// Input: 4-bit data, 0 to 15
// Output: none
	data = (data & (DAC_PINS)) | (GPIO_PORTB_DATA_R & ~(DAC_PINS));
  GPIO_PORTB_DATA_R = data;
}
/* Initialise the DAC hardware for 5.5 KHz wav sound playback */
void Sound_Init(void){
// **************Sound_Init*********************
// Initialize Systick periodic interrupts
// Also calls DAC_Init() to initialize DAC
// Input: none
// Output: none
  DAC_Init();
	NVIC_ST_CTRL_R = 0;
//	NVIC_ST_RELOAD_R = 7526; // 11 KHz sample rate
	NVIC_ST_RELOAD_R = 15052; // 5.5 KHz sample rate
	NVIC_ST_CURRENT_R = 0;
	NVIC_ST_CTRL_R = 7;
}
/* Turn LED1 on or off */
void LED1 (int on) {
	if (on)
		GPIO_PORTB_DATA_R |= BIT(4);
	else
		GPIO_PORTB_DATA_R &= ~(BIT(4));
}
/* Turn LED2 on or off */
void LED2 (int on) {
	if (on)
		GPIO_PORTB_DATA_R |= BIT(5);
	else
		GPIO_PORTB_DATA_R &= ~(BIT(5));
}
/* Start playing a sound */
void Sound_Tone(enum Sound sound){
// **************Sound_Tone*********************
// Change Systick periodic interrupts to start sound output
// Input: interrupt period
//           Units of period are 12.5ns
//           Maximum is 2^24-1
//           Minimum is determined by length of ISR
// Output: none
// this routine sets the RELOAD and starts SysTick
	sound_enabled[sound] = TRUE;
	if (sound == INVADER_SOUND)
		LED1(TRUE);
	else if (sound == SHOOT_SOUND)
		LED2(TRUE);
	DAC_step[sound] = 0;
}
/* Stop playing a sound */
void Sound_Off(int sound){
// **************Sound_Off*********************
// stop outputing to DAC
// Output: none
 // this routine stops the sound output
	sound_enabled[sound] = FALSE;
	if (sound == INVADER_SOUND)
		LED1(FALSE);
	else if (sound == SHOOT_SOUND)
		LED2(FALSE);
}
/* Initialise LED hardware */
void LED_Init(void){
	/* LEDs are on PB4 and PB5 */
	volatile unsigned long delay;
	SYSCTL_RCGC2_R |= SYSCTL_RCGC2_GPIOB; // Enable GPIO Port B clock
	delay = SYSCTL_RCGC2_R;
	GPIO_PORTB_DIR_R |=    (BIT(4)|BIT(5));    // PB4,5 are output
	GPIO_PORTB_AFSEL_R &= ~(BIT(4)|BIT(5));    // Disable alternate function
	GPIO_PORTB_AMSEL_R &= ~(BIT(4)|BIT(5));    // Disable analog functions
	GPIO_PORTB_PCTL_R &= ~(PCTL(4)|PCTL(5));   // Clear PCTL content
	GPIO_PORTB_DEN_R |=    (BIT(4)|BIT(5));    // Enable digital
}
/* Combine active sounds and pass to DAC hardware */
void SysTick_Handler(void){
// Interrupt service routine
// Executed at 11KHz
	int i;
	unsigned long DAC_acc=0;
	int num_sounds_active=0;
	for (i=0; i<MAX_SOUND; i++)
	{
		if (sound_enabled[i])
		{
			unsigned char DAC_val;
			num_sounds_active++;
			DAC_val = *(DAC_wav[i]+(DAC_step[i]/2));
			if (DAC_step[i]%2)
				DAC_acc += DAC_val&0xF;
			else
				DAC_acc += DAC_val>>4;
			if (DAC_step[i]/2 >= DAC_max[i])
				Sound_Off(i);
			DAC_step[i]++;
		}
		if (num_sounds_active > 0)
			DAC_Out(DAC_acc/num_sounds_active);
	}
}
